//used for the Macintosh revised time manager#include "midispec.c"#include "verbomac.c"typedef struct {	TMTask atmTask; 	long tmRefCon; //will pass the address of A5 world} TMInfo, *TMInfoPtr;extern aMEPtr slottedAME;pascal TMInfoPtr GetTMInfo(void)	=0x2E89; TMInfo myTMInfo;void installTMTask(void);pascal void myTask(void);pascal void myTask()	{	long oldA5;	TMInfoPtr recPtr;		recPtr = GetTMInfo();	oldA5 = SetA5(recPtr->tmRefCon);		//	PausePrompt("\ptime in interrupt: ",slottedAME->deltaToGo);	sendAME(slottedAME);		oldA5 = SetA5(oldA5);		PrimeTime((QElemPtr) recPtr, 1);	}void installTMTask(void)	{	myTMInfo.atmTask.tmAddr = myTask;	myTMInfo.atmTask.tmWakeUp = 0;	myTMInfo.atmTask.tmReserved = 0;	myTMInfo.tmRefCon = SetCurrentA5();	InsTime((QElemPtr) &myTMInfo);	}